sys_variable_value.value:
(function execute(inputs, outputs) {
var theETH = inputs.ethValue;
var theBTC = inputs.btcValue;

var theRecH = new GlideRecord("x_405154_cry_hodling");
theRecH.query();

while (theRecH.next()){
   var theTH = new GlideRecord("x_405154_cry_timehodling");
   theTH.initialize();
   theTH.hodling = theRecH.sys_id;
   theTH.value = theRecH.coin.value * theRecH.nocoins;
   theTH.ethvalue = theTH.value/theETH;
   theTH.btcvalue = theTH.value/theBTC;
   theTH.coin_value = theRecH.coin.value;
   theTH.nocoin = theRecH.nocoins;
   theTH.insert();
}
})(inputs, outputs);

